import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './BlogList.css';

const BlogList = () => {
    const [blogs, setBlogs] = useState([]); // Ensure initial state is an empty array
    const [error, setError] = useState('');
    const [newComment, setNewComment] = useState({ username: '', content: '' });

    const fetchBlogs = async () => {
        setLoading(true);
        try {
            const response = await axios.get('http://localhost:5000/api/blogs');
            console.log(response.data); // Log the response to see its structure
            setBlogs(response.data);
        } catch (error) {
            setError('Error fetching blogs: ' + error.message);
        } finally {
            setLoading(false);
        }
    };
    

    const handleLike = async (id) => {
        try {
            await axios.post(`http://localhost:5000/api/blogs/${id}/like`);
            fetchBlogs(); // Refresh the blog list to show updated likes
        } catch (error) {
            console.error('Error liking blog:', error);
        }
    };

    const handleCommentSubmit = async (id) => {
        try {
            await axios.post(`http://localhost:5000/api/blogs/${id}/comments`, newComment);
            setNewComment({ username: '', content: '' }); // Reset comment input
            fetchBlogs(); // Refresh the blog list to show updated comments
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };

    useEffect(() => {
        fetchBlogs();
    }, []);

    return (
        <div className="container">
            {error && <div style={{ color: 'red' }}>{error}</div>}
            {blogs.length > 0 ? (
                blogs.map(blog => (
                    <div className="blog-post" key={blog._id}>
                        <h2>{blog.title}</h2>
                        <img src={blog.image} alt={blog.title} />
                        <p>{blog.content}</p>
                        <button onClick={() => handleLike(blog._id)}>Like ({blog.likes})</button>
                        <h4>Comments:</h4>
                        {blog.comments.map((comment, index) => (
                            <div key={index}>
                                <strong>{comment.username}</strong>: {comment.content}
                            </div>
                        ))}
                        <div>
                            <input
                                type="text"
                                placeholder="Your Name"
                                value={newComment.username}
                                onChange={(e) => setNewComment({ ...newComment, username: e.target.value })}
                            />
                            <input
                                type="text"
                                placeholder="Your Comment"
                                value={newComment.content}
                                onChange={(e) => setNewComment({ ...newComment, content: e.target.value })}
                            />
                            <button onClick={() => handleCommentSubmit(blog._id)}>Submit Comment</button>
                        </div>
                    </div>
                ))
            ) : (
                <div>No blogs available</div>
            )}
        </div>
    );
};

export default BlogList;
